import axios from "axios"
import { ENDPOINT } from "./endpoints";


class EmailApi {

    constructor() { }

    /**
     * Create a new user in the platform
     * @param {oldemail : string, newemail : string} request 
     */
    static async changemail(request) {
        const response = await axios.patch(ENDPOINT.PATCH, request);
        return response.data
    }
}





export default EmailApi
